import React, { useState } from "react";
import LOGO from "../assets/Login.jpeg";
import { Button, Checkbox, Form, Input } from "antd";
const layout = {
    labelCol: {
      span: 8,
    },
    wrapperCol: {
      span: 16,
    },
  };
  
  const tailLayout = {
    wrapperCol: {
      offset: 8,
      span: 16,
    },
  };
export const Login = () => {
    const [form] = Form.useForm();
    const [buttonDisabled, setButtonDisabled] = useState(true);
  
    const onFinish = (values) => {
      console.log('Success:', values);
    };
  
    const onFinishFailed = (errorInfo) => {
      console.log('Failed:', errorInfo);
    };
  
    const handleCheckboxChange = (e) => {
      setButtonDisabled(!e.target.checked);
    };
  return (
    <div className="flex h-screen">
      <div className="w-[40%] ">
        <img src={LOGO} alt="Login image not loading" />
      </div>
      <div className="w-[60%]">
        <div className="flex justify-end p-2 mr-2 gap-2 ">
          Already a member? <a>Sign in</a>
        </div>
        <div className="flex flex-col justify-center items-center mt-[40px]  gap-4">
          <div className="flex flex-col justify-start w-[500px] gap-3">
            <div className="text-2xl  font-semibold">Sign up to Dribble</div>
            {/* <div className="text-xs text-red">Username has already been taken</div> */}
          </div>
          <div className="flex-col flex gap-4 w-[400px] mt-4">
          <Form
      {...layout}
      form={form}
      name="createAccountForm"
      onFinish={onFinish}
      onFinishFailed={onFinishFailed}
    >
      <Form.Item
        label="Name"
        name="name"
        labelCol={{span}}
        rules={[
          {
            required: true,
            message: 'Please input your name!',
          },
        ]}
      >
        <Input />
      </Form.Item>

      <Form.Item
        label="Username"
        name="username"
        rules={[
          {
            required: true,
            message: 'Please input your username!',
          },
        ]}
      >
        <Input />
      </Form.Item>

      <Form.Item
        label="Email"
        name="email"
        rules={[
          {
            type: 'email',
            message: 'Please enter a valid email address!',
          },
          {
            required: true,
            message: 'Please input your email!',
          },
        ]}
      >
        <Input />
      </Form.Item>

      <Form.Item
        label="Password"
        name="password"
        rules={[
          {
            required: true,
            message: 'Please input your password!',
          },
        ]}
      >
        <Input.Password />
      </Form.Item>

      <Form.Item {...tailLayout} name="remember" valuePropName="checked">
        <Checkbox onChange={handleCheckboxChange}>I agree to the terms and conditions</Checkbox>
      </Form.Item>

      <Form.Item {...tailLayout}>
        <Button type="primary" htmlType="submit" disabled={buttonDisabled}>
          Create Account
        </Button>
      </Form.Item>
    </Form>
          </div>
        </div>
      </div>
    </div>
  );
};
